    tempDir = join(...[
      __dirname,
      '..',
      '..',
      'tmp'
    ])
    del.sync([tempDir])
    mkdirp.sync(tempDir)

    httpService = new HttpService({
      baseUrl: 'https://jsonplaceholder.typicode.com/'
    })
  })

  /** @test {HttpService#request} */
  it('should make a successful HTTP request', done => {
    httpService.request('GET', 'posts/1', {
      json: true
    }, true).then(res => {
      expect(res).to.be.an('object')
      done()
    }).catch(done)
  })

