      expect(res).to.be.a('string')
      expect(existsSync(filePath)).to.be.true

      done()
    }).catch(done)
  })

  /**
   * Hook for tearing down the HttpService tests.
   * @type {Function}
   */
  after(() => {
    del.sync(tempDir)
  })
})

